                            <span className="checkbox-custom circular"></span>
                          </label>
                        </li>
                        <li>
                          <label className="checkbox-label">
                            <input
                              type="radio"
                              value="No"
                              id="no"
                              checked={inputs.materials === `No`}
                              onChange={handleYesNo3}
                            />
